import React, { useState } from "react";
import { Plus, X, User, Calendar, MoreHorizontal } from "lucide-react";

const TeamCollaborationBoard = () => {
  const [boards, setBoards] = useState([
    {
      id: 1,
      title: "To Do",
      tasks: [
        {
          id: 1,
          title: "Design user interface",
          assignee: "Alice",
          priority: "High",
          dueDate: "2024-07-15",
        },
        {
          id: 2,
          title: "Setup database",
          assignee: "Bob",
          priority: "Medium",
          dueDate: "2024-07-12",
        },
      ],
    },
    {
      id: 2,
      title: "In Progress",
      tasks: [
        {
          id: 3,
          title: "Implement authentication",
          assignee: "Charlie",
          priority: "High",
          dueDate: "2024-07-10",
        },
      ],
    },
    {
      id: 3,
      title: "Done",
      tasks: [
        {
          id: 4,
          title: "Project planning",
          assignee: "Alice",
          priority: "Low",
          dueDate: "2024-07-05",
        },
      ],
    },
  ]);

  const [teamMembers] = useState(["Alice", "Bob", "Charlie", "David", "Eve"]);
  const [draggedTask, setDraggedTask] = useState(null);
  const [newTaskForm, setNewTaskForm] = useState({
    boardId: null,
    show: false,
  });
  const [newBoardName, setNewBoardName] = useState("");
  const [showNewBoard, setShowNewBoard] = useState(false);

  const handleDragStart = (e, task, boardId) => {
    setDraggedTask({ task, sourceBoardId: boardId });
    e.dataTransfer.effectAllowed = "move";
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = "move";
  };

  const handleDrop = (e, targetBoardId) => {
    e.preventDefault();
    if (!draggedTask) return;

    const { task, sourceBoardId } = draggedTask;
    if (sourceBoardId === targetBoardId) return;

    setBoards((prev) =>
      prev.map((board) => {
        if (board.id === sourceBoardId) {
          return {
            ...board,
            tasks: board.tasks.filter((t) => t.id !== task.id),
          };
        }
        if (board.id === targetBoardId) {
          return { ...board, tasks: [...board.tasks, task] };
        }
        return board;
      })
    );

    setDraggedTask(null);
  };

  const addTask = (boardId, taskData) => {
    const newTask = {
      id: Date.now(),
      title: taskData.title,
      assignee: taskData.assignee,
      priority: taskData.priority,
      dueDate: taskData.dueDate,
    };

    setBoards((prev) =>
      prev.map((board) =>
        board.id === boardId
          ? { ...board, tasks: [...board.tasks, newTask] }
          : board
      )
    );
  };

  const deleteTask = (taskId, boardId) => {
    setBoards((prev) =>
      prev.map((board) =>
        board.id === boardId
          ? { ...board, tasks: board.tasks.filter((t) => t.id !== taskId) }
          : board
      )
    );
  };

  const addBoard = () => {
    if (!newBoardName.trim()) return;

    const newBoard = {
      id: Date.now(),
      title: newBoardName,
      tasks: [],
    };

    setBoards((prev) => [...prev, newBoard]);
    setNewBoardName("");
    setShowNewBoard(false);
  };

  const deleteBoard = (boardId) => {
    setBoards((prev) => prev.filter((board) => board.id !== boardId));
  };

  const TaskCard = ({ task, boardId }) => (
    <div
      draggable
      onDragStart={(e) => handleDragStart(e, task, boardId)}
      className="bg-white rounded-lg p-4 shadow-sm border border-gray-200 cursor-move hover:shadow-md transition-shadow group"
    >
      <div className="flex justify-between items-start mb-2">
        <h4 className="font-medium text-gray-900 text-sm">{task.title}</h4>
        <button
          onClick={() => deleteTask(task.id, boardId)}
          className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-red-500 transition-opacity"
        >
          <X size={16} />
        </button>
      </div>

      <div className="flex items-center gap-2 mb-2">
        <div className="flex items-center gap-1 text-xs text-gray-600">
          <User size={12} />
          <span>{task.assignee}</span>
        </div>
        <span
          className={`px-2 py-1 rounded-full text-xs font-medium ${
            task.priority === "High"
              ? "bg-red-100 text-red-700"
              : task.priority === "Medium"
              ? "bg-yellow-100 text-yellow-700"
              : "bg-green-100 text-green-700"
          }`}
        >
          {task.priority}
        </span>
      </div>

      <div className="flex items-center gap-1 text-xs text-gray-500">
        <Calendar size={12} />
        <span>{task.dueDate}</span>
      </div>
    </div>
  );

  const TaskForm = ({ boardId, onSubmit, onCancel }) => {
    const [formData, setFormData] = useState({
      title: "",
      assignee: teamMembers[0],
      priority: "Medium",
      dueDate: "",
    });

    const handleSubmit = () => {
      if (formData.title.trim()) {
        onSubmit(boardId, formData);
        setFormData({
          title: "",
          assignee: teamMembers[0],
          priority: "Medium",
          dueDate: "",
        });
      }
    };

    const handleKeyPress = (e) => {
      if (e.key === "Enter") {
        handleSubmit();
      }
    };

    return (
      <div className="bg-white rounded-lg p-4 shadow-sm border border-gray-200">
        <input
          type="text"
          placeholder="Task title..."
          value={formData.title}
          onChange={(e) =>
            setFormData((prev) => ({ ...prev, title: e.target.value }))
          }
          onKeyPress={handleKeyPress}
          className="w-full p-2 border border-gray-300 rounded mb-3 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
          autoFocus
        />

        <div className="grid grid-cols-2 gap-2 mb-3">
          <select
            value={formData.assignee}
            onChange={(e) =>
              setFormData((prev) => ({ ...prev, assignee: e.target.value }))
            }
            className="p-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {teamMembers.map((member) => (
              <option key={member} value={member}>
                {member}
              </option>
            ))}
          </select>

          <select
            value={formData.priority}
            onChange={(e) =>
              setFormData((prev) => ({ ...prev, priority: e.target.value }))
            }
            className="p-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="High">High</option>
            <option value="Medium">Medium</option>
            <option value="Low">Low</option>
          </select>
        </div>

        <input
          type="date"
          value={formData.dueDate}
          onChange={(e) =>
            setFormData((prev) => ({ ...prev, dueDate: e.target.value }))
          }
          className="w-full p-2 border border-gray-300 rounded mb-3 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        <div className="flex gap-2">
          <button
            onClick={handleSubmit}
            className="flex-1 bg-blue-600 text-white py-2 px-4 rounded text-sm hover:bg-blue-700 transition-colors"
          >
            Add Task
          </button>
          <button
            onClick={onCancel}
            className="px-4 py-2 text-gray-600 hover:text-gray-800 text-sm"
          >
            Cancel
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex justify-between items-center">
            <h1 className="text-2xl font-bold text-gray-900">
              Team Collaboration Board
            </h1>
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <span className="text-sm text-gray-600">Team:</span>
                <div className="flex -space-x-2">
                  {teamMembers.slice(0, 4).map((member, index) => (
                    <div
                      key={member}
                      className="w-8 h-8 rounded-full bg-blue-500 flex items-center justify-center text-white text-xs font-medium border-2 border-white"
                      title={member}
                    >
                      {member[0]}
                    </div>
                  ))}
                  {teamMembers.length > 4 && (
                    <div className="w-8 h-8 rounded-full bg-gray-500 flex items-center justify-center text-white text-xs font-medium border-2 border-white">
                      +{teamMembers.length - 4}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-6">
        <div className="flex gap-6 overflow-x-auto">
          {boards.map((board) => (
            <div
              key={board.id}
              className="flex-shrink-0 w-80 bg-gray-100 rounded-lg p-4"
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, board.id)}
            >
              <div className="flex justify-between items-center mb-4">
                <h3 className="font-semibold text-gray-800">{board.title}</h3>
                <div className="flex items-center gap-2">
                  <span className="bg-gray-200 text-gray-700 px-2 py-1 rounded-full text-xs">
                    {board.tasks.length}
                  </span>
                  <button
                    onClick={() => deleteBoard(board.id)}
                    className="text-gray-400 hover:text-red-500 transition-colors"
                  >
                    <X size={16} />
                  </button>
                </div>
              </div>

              <div className="space-y-3 mb-4">
                {board.tasks.map((task) => (
                  <TaskCard key={task.id} task={task} boardId={board.id} />
                ))}
              </div>

              {newTaskForm.show && newTaskForm.boardId === board.id ? (
                <TaskForm
                  boardId={board.id}
                  onSubmit={addTask}
                  onCancel={() =>
                    setNewTaskForm({ boardId: null, show: false })
                  }
                />
              ) : (
                <button
                  onClick={() =>
                    setNewTaskForm({ boardId: board.id, show: true })
                  }
                  className="w-full flex items-center justify-center gap-2 p-3 border-2 border-dashed border-gray-300 rounded-lg text-gray-500 hover:border-gray-400 hover:text-gray-600 transition-colors"
                >
                  <Plus size={16} />
                  Add a task
                </button>
              )}
            </div>
          ))}

          {showNewBoard ? (
            <div className="flex-shrink-0 w-80 bg-gray-100 rounded-lg p-4">
              <input
                type="text"
                placeholder="Board name..."
                value={newBoardName}
                onChange={(e) => setNewBoardName(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded mb-3 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                autoFocus
              />
              <div className="flex gap-2">
                <button
                  onClick={addBoard}
                  className="flex-1 bg-blue-600 text-white py-2 px-4 rounded text-sm hover:bg-blue-700 transition-colors"
                >
                  Add Board
                </button>
                <button
                  onClick={() => setShowNewBoard(false)}
                  className="px-4 py-2 text-gray-600 hover:text-gray-800 text-sm"
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <button
              onClick={() => setShowNewBoard(true)}
              className="flex-shrink-0 w-80 h-32 flex items-center justify-center gap-2 border-2 border-dashed border-gray-300 rounded-lg text-gray-500 hover:border-gray-400 hover:text-gray-600 transition-colors"
            >
              <Plus size={20} />
              Add another board
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default TeamCollaborationBoard;
