// Column component
const Column = ({ title, tasks, onUpdateTask, onDeleteTask }) => (
  <div style={styles.column}>
    <h3>{title}</h3>
    <div style={styles.tasks}>
      {tasks.map((task) => (
        <Task
          key={task._id}
          task={task}
          onUpdate={onUpdateTask}
          onDelete={onDeleteTask}
        />
      ))}
    </div>
  </div>
);

// New Task Form
const NewTaskForm = ({ boardId, onTaskCreated }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [taskData, setTaskData] = useState({
    title: "",
    description: "",
    status: "To Do",
    priority: "Medium",
    assignedTo: "",
    dueDate: "",
  });

  const handleSubmit = async () => {
    if (!taskData.title.trim()) return;

    try {
      const response = await fetch(`${API_BASE}/boards/${boardId}/tasks`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(taskData),
      });

      if (response.ok) {
        const newTask = await response.json();
        onTaskCreated(newTask);
        setTaskData({
          title: "",
          description: "",
          status: "To Do",
          priority: "Medium",
          assignedTo: "",
          dueDate: "",
        });
        setIsOpen(false);
      }
    } catch (error) {
      console.error("Error creating task:", error);
    }
  };

  if (!isOpen) {
    return (
      <button style={styles.newTaskBtn} onClick={() => setIsOpen(true)}>
        + Add New Task
      </button>
    );
  }

  return (
    <div style={styles.newTaskForm}>
      <input
        type="text"
        value={taskData.title}
        onChange={(e) => setTaskData({ ...taskData, title: e.target.value })}
        placeholder="Task title"
        style={styles.input}
      />
      <textarea
        value={taskData.description}
        onChange={(e) =>
          setTaskData({ ...taskData, description: e.target.value })
        }
        placeholder="Description"
        style={styles.textarea}
      />
      <select
        value={taskData.status}
        onChange={(e) => setTaskData({ ...taskData, status: e.target.value })}
        style={styles.select}
      >
        <option value="To Do">To Do</option>
        <option value="In Progress">In Progress</option>
        <option value="Done">Done</option>
      </select>
      <select
        value={taskData.priority}
        onChange={(e) => setTaskData({ ...taskData, priority: e.target.value })}
        style={styles.select}
      >
        <option value="Low">Low</option>
        <option value="Medium">Medium</option>
        <option value="High">High</option>
      </select>
      <input
        type="text"
        value={taskData.assignedTo}
        onChange={(e) =>
          setTaskData({ ...taskData, assignedTo: e.target.value })
        }
        placeholder="Assigned to"
        style={styles.input}
      />
      <input
        type="date"
        value={taskData.dueDate}
        onChange={(e) => setTaskData({ ...taskData, dueDate: e.target.value })}
        style={styles.input}
      />
      <div style={styles.formActions}>
        <button onClick={handleSubmit} style={styles.button}>
          Create Task
        </button>
        <button onClick={() => setIsOpen(false)} style={styles.buttonSecondary}>
          Cancel
        </button>
      </div>
    </div>
  );
