const NewBoardForm = ({ onBoardCreated }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [boardData, setBoardData] = useState({ name: "", description: "" });

  const handleSubmit = async () => {
    if (!boardData.name.trim()) return;

    try {
      const response = await fetch(`${API_BASE}/boards`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(boardData),
      });

      if (response.ok) {
        const newBoard = await response.json();
        onBoardCreated(newBoard);
        setBoardData({ name: "", description: "" });
        setIsOpen(false);
      }
    } catch (error) {
      console.error("Error creating board:", error);
    }
  };

  if (!isOpen) {
    return (
      <button style={styles.newBoardBtn} onClick={() => setIsOpen(true)}>
        + New Board
      </button>
    );
  }

  return (
    <div style={styles.newBoardForm}>
      <input
        type="text"
        value={boardData.name}
        onChange={(e) => setBoardData({ ...boardData, name: e.target.value })}
        placeholder="Board name"
        style={styles.input}
      />
      <textarea
        value={boardData.description}
        onChange={(e) =>
          setBoardData({ ...boardData, description: e.target.value })
        }
        placeholder="Board description"
        style={styles.textarea}
      />
      <div style={styles.formActions}>
        <button onClick={handleSubmit} style={styles.button}>
          Create Board
        </button>
        <button onClick={() => setIsOpen(false)} style={styles.buttonSecondary}>
          Cancel
        </button>
      </div>
    </div>
  );
};

const TeamCollaborationBoard = () => {
  const [boards, setBoards] = useState([]);
  const [selectedBoard, setSelectedBoard] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Load boards on mount
  useEffect(() => {
    const fetchBoards = async () => {
      try {
        const response = await fetch(`${API_BASE}/boards`);
        const data = await response.json();
        setBoards(data);

        // Auto-select first board if available
        if (data.length > 0 && !selectedBoard) {
          setSelectedBoard(data[0]);
        }
      } catch (error) {
        console.error("Error fetching boards:", error);
      }
    };

    fetchBoards();
  }, [selectedBoard]);

  // Load tasks when board is selected
  useEffect(() => {
    if (selectedBoard) {
      const fetchTasks = async () => {
        try {
          const response = await fetch(
            `${API_BASE}/boards/${selectedBoard._id}/tasks`
          );
          const data = await response.json();
          setTasks(data);
        } catch (error) {
          console.error("Error fetching tasks:", error);
        }
      };

      fetchTasks();
    }
  }, [selectedBoard]);

  const handleBoardSelect = (board) => {
    setSelectedBoard(board);
  };

  const handleBoardCreated = (newBoard) => {
    setBoards([newBoard, ...boards]);
    setSelectedBoard(newBoard);
  };

  const handleTaskCreated = (newTask) => {
    setTasks([newTask, ...tasks]);
  };

  const handleTaskUpdate = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task._id === updatedTask._id ? updatedTask : task))
    );
  };

  const handleTaskDelete = (taskId) => {
    setTasks(tasks.filter((task) => task._id !== taskId));
  };

  const todoTasks = tasks.filter((task) => task.status === "To Do");
  const inProgressTasks = tasks.filter((task) => task.status === "In Progress");
  const doneTasks = tasks.filter((task) => task.status === "Done");

  return (
    <div style={styles.app}>
      <div
        style={{
          ...styles.sidebar,
          ...(sidebarOpen ? styles.sidebarOpen : styles.sidebarClosed),
        }}
      >
        <div style={styles.sidebarHeader}>
          <h2>Boards</h2>
          <button
            style={styles.sidebarToggle}
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            {sidebarOpen ? "‚Üê" : "‚Üí"}
          </button>
        </div>

        {sidebarOpen && (
          <div style={styles.sidebarContent}>
            <NewBoardForm onBoardCreated={handleBoardCreated} />
            <div style={styles.boardsList}>
              {boards.map((board) => (
                <Board
                  key={board._id}
                  board={board}
                  onSelect={handleBoardSelect}
                  isSelected={selectedBoard?._id === board._id}
                />
              ))}
            </div>
          </div>
        )}
      </div>

      <div style={styles.mainContent}>
        {selectedBoard ? (
          <>
            <div style={styles.boardHeader}>
              <h1>{selectedBoard.name}</h1>
              {selectedBoard.description && <p>{selectedBoard.description}</p>}
              <NewTaskForm
                boardId={selectedBoard._id}
                onTaskCreated={handleTaskCreated}
              />
            </div>

            <div style={styles.boardColumns}>
              <Column
                title="To Do"
                tasks={todoTasks}
                onUpdateTask={handleTaskUpdate}
                onDeleteTask={handleTaskDelete}
              />
              <Column
                title="In Progress"
                tasks={inProgressTasks}
                onUpdateTask={handleTaskUpdate}
                onDeleteTask={handleTaskDelete}
              />
              <Column
                title="Done"
                tasks={doneTasks}
                onUpdateTask={handleTaskUpdate}
                onDeleteTask={handleTaskDelete}
              />
            </div>
          </>
        ) : (
          <div style={styles.noBoard}>
            <h2>Welcome to Team Collaboration Board</h2>
            <p>
              Select a board from the sidebar or create a new one to get
              started!
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

