import React, { useState, useEffect } from "react";

const API_BASE = "http://localhost:5000";

// Priority colors
const PRIORITY_COLORS = {
  Low: "#28a745",
  Medium: "#ffc107",
  High: "#dc3545",
};

// Board component
const Board = ({ board, onSelect, isSelected }) => (
  <div
    style={{
      ...styles.boardItem,
      ...(isSelected ? styles.boardItemSelected : {}),
    }}
    onClick={() => onSelect(board)}
  >
    <h4>{board.name}</h4>
    {board.description && <p>{board.description}</p>}
  </div>
);

// Task component
const Task = ({ task, onUpdate, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({
    title: task.title,
    description: task.description,
    priority: task.priority,
    assignedTo: task.assignedTo,
    dueDate: task.dueDate ? task.dueDate.split("T")[0] : "",
  });

  const handleSave = async () => {
    try {
      const response = await fetch(`${API_BASE}/tasks/${task._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editData),
      });

      if (response.ok) {
        const updatedTask = await response.json();
        onUpdate(updatedTask);
        setIsEditing(false);
      }
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  const handleStatusChange = async (newStatus) => {
    try {
      const response = await fetch(`${API_BASE}/tasks/${task._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });

      if (response.ok) {
        const updatedTask = await response.json();
        onUpdate(updatedTask);
      }
    } catch (error) {
      console.error("Error updating task status:", error);
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      try {
        await fetch(`${API_BASE}/tasks/${task._id}`, { method: "DELETE" });
        onDelete(task._id);
      } catch (error) {
        console.error("Error deleting task:", error);
      }
    }
  };

  if (isEditing) {
    return (
      <div style={{ ...styles.task, ...styles.taskEditing }}>
        <input
          type="text"
          value={editData.title}
          onChange={(e) => setEditData({ ...editData, title: e.target.value })}
          placeholder="Task title"
          style={styles.input}
        />
        <textarea
          value={editData.description}
          onChange={(e) =>
            setEditData({ ...editData, description: e.target.value })
          }
          placeholder="Description"
          style={styles.textarea}
        />
        <select
          value={editData.priority}
          onChange={(e) =>
            setEditData({ ...editData, priority: e.target.value })
          }
          style={styles.select}
        >
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <input
          type="text"
          value={editData.assignedTo}
          onChange={(e) =>
            setEditData({ ...editData, assignedTo: e.target.value })
          }
          placeholder="Assigned to"
          style={styles.input}
        />
        <input
          type="date"
          value={editData.dueDate}
          onChange={(e) =>
            setEditData({ ...editData, dueDate: e.target.value })
          }
          style={styles.input}
        />
        <div style={styles.taskActions}>
          <button onClick={handleSave} style={styles.button}>
            Save
          </button>
          <button
            onClick={() => setIsEditing(false)}
            style={styles.buttonSecondary}
          >
            Cancel
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.task}>
      <div style={styles.taskHeader}>
        <h4>{task.title}</h4>
        <span
          style={{
            ...styles.priorityBadge,
            backgroundColor: PRIORITY_COLORS[task.priority],
          }}
        >
          {task.priority}
        </span>
      </div>
      {task.description && <p>{task.description}</p>}
      {task.assignedTo && (
        <div style={styles.assignedTo}>👤 {task.assignedTo}</div>
      )}
      {task.dueDate && (
        <div style={styles.dueDate}>
          📅 {new Date(task.dueDate).toLocaleDateString()}
        </div>
      )}

      <div style={styles.taskControls}>
        <select
          value={task.status}
          onChange={(e) => handleStatusChange(e.target.value)}
          style={styles.select}
        >
          <option value="To Do">To Do</option>
          <option value="In Progress">In Progress</option>
          <option value="Done">Done</option>
        </select>
        <button onClick={() => setIsEditing(true)} style={styles.button}>
          Edit
        </button>
        <button onClick={handleDelete} style={styles.buttonDanger}>
          Delete
        </button>
      </div>
    </div>
  );
};

// Column component
const Column = ({ title, tasks, onUpdateTask, onDeleteTask }) => (
  <div style={styles.column}>
    <h3>{title}</h3>
    <div style={styles.tasks}>
      {tasks.map((task) => (
        <Task
          key={task._id}
          task={task}
          onUpdate={onUpdateTask}
          onDelete={onDeleteTask}
        />
      ))}
    </div>
  </div>
);

// New Task Form
const NewTaskForm = ({ boardId, onTaskCreated }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [taskData, setTaskData] = useState({
    title: "",
    description: "",
    status: "To Do",
    priority: "Medium",
    assignedTo: "",
    dueDate: "",
  });

  const handleSubmit = async () => {
    if (!taskData.title.trim()) return;

    try {
      const response = await fetch(`${API_BASE}/boards/${boardId}/tasks`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(taskData),
      });

      if (response.ok) {
        const newTask = await response.json();
        onTaskCreated(newTask);
        setTaskData({
          title: "",
          description: "",
          status: "To Do",
          priority: "Medium",
          assignedTo: "",
          dueDate: "",
        });
        setIsOpen(false);
      }
    } catch (error) {
      console.error("Error creating task:", error);
    }
  };

  if (!isOpen) {
    return (
      <button style={styles.newTaskBtn} onClick={() => setIsOpen(true)}>
        + Add New Task
      </button>
    );
  }

  return (
    <div style={styles.newTaskForm}>
      <input
        type="text"
        value={taskData.title}
        onChange={(e) => setTaskData({ ...taskData, title: e.target.value })}
        placeholder="Task title"
        style={styles.input}
      />
      <textarea
        value={taskData.description}
        onChange={(e) =>
          setTaskData({ ...taskData, description: e.target.value })
        }
        placeholder="Description"
        style={styles.textarea}
      />
      <select
        value={taskData.status}
        onChange={(e) => setTaskData({ ...taskData, status: e.target.value })}
        style={styles.select}
      >
        <option value="To Do">To Do</option>
        <option value="In Progress">In Progress</option>
        <option value="Done">Done</option>
      </select>
      <select
        value={taskData.priority}
        onChange={(e) => setTaskData({ ...taskData, priority: e.target.value })}
        style={styles.select}
      >
        <option value="Low">Low</option>
        <option value="Medium">Medium</option>
        <option value="High">High</option>
      </select>
      <input
        type="text"
        value={taskData.assignedTo}
        onChange={(e) =>
          setTaskData({ ...taskData, assignedTo: e.target.value })
        }
        placeholder="Assigned to"
        style={styles.input}
      />
      <input
        type="date"
        value={taskData.dueDate}
        onChange={(e) => setTaskData({ ...taskData, dueDate: e.target.value })}
        style={styles.input}
      />
      <div style={styles.formActions}>
        <button onClick={handleSubmit} style={styles.button}>
          Create Task
        </button>
        <button onClick={() => setIsOpen(false)} style={styles.buttonSecondary}>
          Cancel
        </button>
      </div>
    </div>
  );
};

// New Board Form
const NewBoardForm = ({ onBoardCreated }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [boardData, setBoardData] = useState({ name: "", description: "" });

  const handleSubmit = async () => {
    if (!boardData.name.trim()) return;

    try {
      const response = await fetch(`${API_BASE}/boards`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(boardData),
      });

      if (response.ok) {
        const newBoard = await response.json();
        onBoardCreated(newBoard);
        setBoardData({ name: "", description: "" });
        setIsOpen(false);
      }
    } catch (error) {
      console.error("Error creating board:", error);
    }
  };

  if (!isOpen) {
    return (
      <button style={styles.newBoardBtn} onClick={() => setIsOpen(true)}>
        + New Board
      </button>
    );
  }

  return (
    <div style={styles.newBoardForm}>
      <input
        type="text"
        value={boardData.name}
        onChange={(e) => setBoardData({ ...boardData, name: e.target.value })}
        placeholder="Board name"
        style={styles.input}
      />
      <textarea
        value={boardData.description}
        onChange={(e) =>
          setBoardData({ ...boardData, description: e.target.value })
        }
        placeholder="Board description"
        style={styles.textarea}
      />
      <div style={styles.formActions}>
        <button onClick={handleSubmit} style={styles.button}>
          Create Board
        </button>
        <button onClick={() => setIsOpen(false)} style={styles.buttonSecondary}>
          Cancel
        </button>
      </div>
    </div>
  );
};

// Main App component
const App = () => {
  const [boards, setBoards] = useState([]);
  const [selectedBoard, setSelectedBoard] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Load boards on mount
  useEffect(() => {
    const fetchBoards = async () => {
      try {
        const response = await fetch(`${API_BASE}/boards`);
        const data = await response.json();
        setBoards(data);

        // Auto-select first board if available
        if (data.length > 0 && !selectedBoard) {
          setSelectedBoard(data[0]);
        }
      } catch (error) {
        console.error("Error fetching boards:", error);
      }
    };

    fetchBoards();
  }, [selectedBoard]);

  // Load tasks when board is selected
  useEffect(() => {
    if (selectedBoard) {
      const fetchTasks = async () => {
        try {
          const response = await fetch(
            `${API_BASE}/boards/${selectedBoard._id}/tasks`
          );
          const data = await response.json();
          setTasks(data);
        } catch (error) {
          console.error("Error fetching tasks:", error);
        }
      };

      fetchTasks();
    }
  }, [selectedBoard]);

  const handleBoardSelect = (board) => {
    setSelectedBoard(board);
  };

  const handleBoardCreated = (newBoard) => {
    setBoards([newBoard, ...boards]);
    setSelectedBoard(newBoard);
  };

  const handleTaskCreated = (newTask) => {
    setTasks([newTask, ...tasks]);
  };

  const handleTaskUpdate = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task._id === updatedTask._id ? updatedTask : task))
    );
  };

  const handleTaskDelete = (taskId) => {
    setTasks(tasks.filter((task) => task._id !== taskId));
  };

  const todoTasks = tasks.filter((task) => task.status === "To Do");
  const inProgressTasks = tasks.filter((task) => task.status === "In Progress");
  const doneTasks = tasks.filter((task) => task.status === "Done");

  return (
    <div style={styles.app}>
      <div
        style={{
          ...styles.sidebar,
          ...(sidebarOpen ? styles.sidebarOpen : styles.sidebarClosed),
        }}
      >
        <div style={styles.sidebarHeader}>
          <h2>Boards</h2>
          <button
            style={styles.sidebarToggle}
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            {sidebarOpen ? "←" : "→"}
          </button>
        </div>

        {sidebarOpen && (
          <div style={styles.sidebarContent}>
            <NewBoardForm onBoardCreated={handleBoardCreated} />
            <div style={styles.boardsList}>
              {boards.map((board) => (
                <Board
                  key={board._id}
                  board={board}
                  onSelect={handleBoardSelect}
                  isSelected={selectedBoard?._id === board._id}
                />
              ))}
            </div>
          </div>
        )}
      </div>

      <div style={styles.mainContent}>
        {selectedBoard ? (
          <>
            <div style={styles.boardHeader}>
              <h1>{selectedBoard.name}</h1>
              {selectedBoard.description && <p>{selectedBoard.description}</p>}
              <NewTaskForm
                boardId={selectedBoard._id}
                onTaskCreated={handleTaskCreated}
              />
            </div>

            <div style={styles.boardColumns}>
              <Column
                title="To Do"
                tasks={todoTasks}
                onUpdateTask={handleTaskUpdate}
                onDeleteTask={handleTaskDelete}
              />
              <Column
                title="In Progress"
                tasks={inProgressTasks}
                onUpdateTask={handleTaskUpdate}
                onDeleteTask={handleTaskDelete}
              />
              <Column
                title="Done"
                tasks={doneTasks}
                onUpdateTask={handleTaskUpdate}
                onDeleteTask={handleTaskDelete}
              />
            </div>
          </>
        ) : (
          <div style={styles.noBoard}>
            <h2>Welcome to Team Collaboration Board</h2>
            <p>
              Select a board from the sidebar or create a new one to get
              started!
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

// Styles
const styles = {
  app: {
    display: "flex",
    height: "100vh",
    fontFamily: "Arial, sans-serif",
    backgroundColor: "#f5f5f5",
  },
  sidebar: {
    backgroundColor: "#2c3e50",
    color: "white",
    transition: "width 0.3s ease",
  },
